var co = require('co')
var Stream = require('stream')
var assert = require('assert')

var busboy = require('./')

describe('Co Busboy', function () {
  it('should work', function (done) {
    // https://github.com/mscdex/busboy/blob/master/test/test-types-multipart.js

    var request = new Stream.PassThrough()
    request.headers = {
      'content-type': 'multipart/form-data; boundary=---------------------------paZqsnEHRufoShdX6fh0lUhXBP4k'
    }
    request.end([
      '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
      'Content-Disposition: form-data; name="file_name_0"',
      '',
      'super alpha file',
      '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
      'Content-Disposition: form-data; name="file_name_1"',
      '',
      'super beta file',
      '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
      'Content-Disposition: form-data; name="upload_file_0"; filename="1k_a.dat"',
      'Content-Type: application/octet-stream',
      '',
      '',
      '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k',
      'Content-Disposition: form-data; name="upload_file_1"; filename="1k_b.dat"',
      'Content-Type: application/octet-stream',
      '',
      '',
     '-----------------------------paZqsnEHRufoShdX6fh0lUhXBP4k--'
    ].join('\r\n'))

    co(function*(){
      var parser = busboy(request);
      var part;
      var count = 0
      while (part = yield parser.part()) {
        count++
        part.resume()
      }
      assert.equal(count, 2)
    })(done)
  })
})